function c(t=""){return t.split(" ").map(n=>n[0]).slice(0,2).join("").toUpperCase()}function i(t){try{return new Date(t).toLocaleDateString("en-US",{month:"short",day:"numeric",year:"numeric"})}catch{return String(t)}}function u(t){try{return new Date(t).toLocaleTimeString("en-US",{hour:"2-digit",minute:"2-digit"})}catch{return String(t)}}function g(t){try{const n=new Date,e=new Date(t),r=n-e,o=Math.floor(r/6e4),a=Math.floor(r/36e5),s=Math.floor(r/864e5);return o<1?"Just now":o<60?`${o}m ago`:a<24?`${a}h ago`:`${s}d ago`}catch{return String(t)}}function f(){const t=new Date().getHours();return t<12?"Good morning! Ready to learn something new today?":t<18?"Good afternoon! Hope your day is going well.":"Good evening! Time to wrap up your work."}function d(t){return{...{admin:{totalUsers:0,activeClasses:0,flaggedAssignments:0,pendingEnrollments:0,teacherCount:0,studentCount:0,adminCount:0,systemHealth:98,avgEngagement:85,userTrend:"+5%",classTrend:"+12%",serverStatus:"Online",dbStatus:"Connected",storageUsage:65,activeSessions:42},teacher:{activeClasses:0,totalStudents:0,totalAssignments:0,pendingGrading:0,avgCompletion:0,attendanceRate:92,classTrend:"+2%",studentTrend:"+5%",assignmentTrend:"+8%"},student:{enrolledClasses:0,upcomingAssignments:0,assignmentsDueToday:0,completedAssignments:0,averageGrade:0,attendanceRate:95}}[t]}}function l(t,n=null){try{const e=localStorage.getItem(t);return e?JSON.parse(e):n}catch{return n}}function m(t){if(!t)return"No upcoming events";const e=t-new Date;if(e<=0)return"Starting soon";const r=Math.floor(e/(1e3*60*60)),o=Math.floor(e%(1e3*60*60)/(1e3*60));return`${r}h ${o}m`}function h(t){try{const n=new Date(t),e=new Date,r=n.getTime()-e.getTime(),o=Math.ceil(r/(1e3*60*60*24));return o===0?"Today":o===1?"Tomorrow":o<7?`In ${o} days`:i(n)}catch{return String(t)}}export{d as a,i as b,g as c,f as d,h as e,u as f,c as g,m as h,l as s};
